{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyO4p8OrEPdp5YZn7pT3/Zzf"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"lnsYutVbYiSi"},"outputs":[],"source":["import streamlit as st\n","import pandas as pd\n","import numpy as np\n","import pickle\n","from utils import preprocess_data, predict_price\n","from PIL import Image\n","\n","# Load the saved model and scaler\n","with open('models/model.pkl', 'rb') as f:\n","    model = pickle.load(f)\n","\n","with open('models/scaler.pkl', 'rb') as f:\n","    scaler = pickle.load(f)\n","\n","# Load a sample image for the logo (optional)\n","logo = Image.open(\"logo.png\")\n","\n","# Streamlit app\n","def main():\n","    st.image(logo, use_column_width=True)\n","    st.title(\"Jewelry Price Prediction\")\n","\n","    # Create a sidebar for navigation\n","    st.sidebar.title(\"Navigation\")\n","    selected_page = st.sidebar.radio(\"Go to\", [\"Home\", \"Prediction\", \"About\"])\n","\n","    if selected_page == \"Home\":\n","        st.write(\"Welcome to the Jewelry Price Prediction App!\")\n","        st.write(\"This app uses a machine learning model to predict the price of jewelry based on various features.\")\n","\n","    elif selected_page == \"Prediction\":\n","        st.header(\"Predict Jewelry Price\")\n","\n","        # Create input fields for user input\n","        category = st.selectbox(\"Category\", [\"Necklace\", \"Ring\", \"Earrings\", \"Bracelet\"])\n","        brand_id = st.text_input(\"Brand ID\")\n","        target_gender = st.selectbox(\"Target Gender\", [\"Male\", \"Female\", \"Unisex\"])\n","        main_color = st.text_input(\"Main Color\")\n","        main_metal = st.selectbox(\"Main Metal\", [\"Gold\", \"Silver\", \"Platinum\", \"Other\"])\n","        main_gem = st.text_input(\"Main Gem\")\n","        order_month = st.number_input(\"Order Month (1-12)\", min_value=1, max_value=12)\n","        order_day = st.number_input(\"Order Day (1-31)\", min_value=1, max_value=31)\n","\n","        # Create a button for prediction\n","        if st.button(\"Predict Price\"):\n","            # Prepare input data\n","            input_data = {\n","                \"Category\": category,\n","                \"Brand_ID\": brand_id,\n","                \"Target_Gender\": target_gender,\n","                \"Main_Color\": main_color,\n","                \"Main_Metal\": main_metal,\n","                \"Main_Gem\": main_gem,\n","                \"Order_Month\": order_month,\n","                \"Order_Day\": order_day\n","            }\n","\n","            # Preprocess the input data\n","            preprocessed_data = preprocess_data(input_data)\n","\n","            # Make prediction\n","            predicted_price = predict_price(model, scaler, preprocessed_data)\n","\n","            # Display the predicted price\n","            st.success(f\"Predicted Price: ${predicted_price:.2f}\")\n","\n","    elif selected_page == \"About\":\n","        st.header(\"About\")\n","        st.write(\"This application uses a machine learning model to predict the price of jewelry.\")\n","        st.write(\"Developed by: [Your Name]\")\n","\n","if __name__ == \"__main__\":\n","    main()"]}]}